function findAnagrams(s, p) {
  const pCount = {};

  for (let i = 0; i < p.length; i++) {
    const char = p[i];
    pCount[char] = (pCount[char] || 0) + 1;
  }

  const result = [];
  const windowSize = p.length;
  const sCount = {};

  for (let i = 0; i < windowSize; i++) {
    const char = s[i];
    sCount[char] = (sCount[char] || 0) + 1;
  }

  let matches = true;
  for (let char in pCount) {
    if (sCount[char] !== pCount[char]) {
      matches = false;
      break;
    }
  }
  if (matches) {
    result.push(0);
  }

  // Slide window
  for (let i = windowSize; i < s.length; i++) {
    // Remove leftmost character
    const leftChar = s[i - windowSize];
    sCount[leftChar]--;
    if (sCount[leftChar] === 0) {
      delete sCount[leftChar];
    }

    // Add rightmost character
    const rightChar = s[i];
    sCount[rightChar] = (sCount[rightChar] || 0) + 1;

    // Check if frequencies match
    matches = true;
    for (let char in pCount) {
      if (sCount[char] !== pCount[char]) {
        matches = false;
        break;
      }
    }
    if (matches) {
      result.push(i - windowSize + 1);
    }
  }
}

