function postorderTraversal(root) {
  const result = [];

  function traverse(node) {
    if (node === null) return;

    traverse(node.left);   // Left first
    traverse(node.right);  // Right second
    result.push(node.val); // Root LAST (postorder)
  }

  traverse(root);
  return result;
}
