function plusOne(digits) {
  let carry = 1;

  for (let i = digits.length - 1; i >= 0; i--) {
    let sum = digits[i] + carry;
    digits[i] = sum % 10;
    carry = Math.floor(sum / 10);

    if (carry === 0) break;
  }

  // If carry is still 1, we need a new array
  if (carry === 1) {
    return [1, ...digits];
  }

  return digits;
}

function plusOne(digits) {
  let carry = 1;

  for (let i = digits.length - 1; i >= 0; i--) {
    digits[i] += carry;

    if (digits[i] === 10) {
      digits[i] = 0;
      carry = 1;
    } else {
      carry = 0;
      break;
    }
  }

  if (carry === 1) {
    digits.unshift(1);
  }

  return digits;
}
