function radixSort(arr) {
  if (arr.length === 0) return arr;

  // Find maximum number
  let max = arr[0];
  for (let i = 1; i < arr.length; i++) {
    if (arr[i] > max) max = arr[i];
  }

function radixSort(arr) {
  // ... find max ...

  // Process each digit position
  for (let exp = 1; Math.floor(max / exp) > 0; exp *= 10) {
    arr = countingSortByDigit(arr, exp);
  }

  return arr;

