function isAnagram(s, t) {
  if (s.length !== t.length) {
    return false;
  }

  let freq = {};

  for (let char of s) {
    freq[char] = (freq[char] || 0) + 1;
  }

  // Count characters in t (decrement)
  for (let char of t) {
    freq[char] = (freq[char] || 0) - 1;
  }
  // After processing "nagaram": freq = {a:0, n:0, g:0, r:0, m:0}
}

function isAnagram(s, t) {
  if (s.length !== t.length) {
    return false;
  }

  let freq = {};

  for (let char of s) {
    freq[char] = (freq[char] || 0) + 1;
  }

  for (let char of t) {
    freq[char] = (freq[char] || 0) - 1;
  }

  // Check if all counts are zero
  for (let char in freq) {
    if (freq[char] !== 0) {
      return false;
    }
  }

  return true;
}
