function totalFruit(fruits) {
  let left = 0;
  let maxLen = 0;
  const freq = {};

  for (let right = 0; right < fruits.length; right++) {
    const type = fruits[right];
    freq[type] = (freq[type] || 0) + 1;

    // Shrink window if we have more than 2 distinct types
    while (Object.keys(freq).length > 2) {
      const leftType = fruits[left];
      freq[leftType]--;
      if (freq[leftType] === 0) {
        delete freq[leftType];
      }
      left++;
    }

  }
}

function totalFruit(fruits) {
  let left = 0;
  let maxLen = 0;
  const freq = {};

  for (let right = 0; right < fruits.length; right++) {
    const type = fruits[right];
    freq[type] = (freq[type] || 0) + 1;

    while (Object.keys(freq).length > 2) {
      const leftType = fruits[left];
      freq[leftType]--;
      if (freq[leftType] === 0) {
        delete freq[leftType];
      }
      left++;
    }

    // Update maxLen with current window size
    maxLen = Math.max(maxLen, right - left + 1);
  }
}

function totalFruit(fruits) {
  let left = 0;
  let maxLen = 0;
  const freq = {};

  for (let right = 0; right < fruits.length; right++) {
    const type = fruits[right];
    freq[type] = (freq[type] || 0) + 1;

    while (Object.keys(freq).length > 2) {
      const leftType = fruits[left];
      freq[leftType]--;
      if (freq[leftType] === 0) {
        delete freq[leftType];
      }
      left++;
    }

    maxLen = Math.max(maxLen, right - left + 1);
  }

  return maxLen;
}
