function groupAnagrams(strs) {
  const map = new Map();

  for (let str of strs) {
    const sorted = str.split('').sort().join('');

    if (!map.has(sorted)) {
      map.set(sorted, []);
    }

    // Add original string to the group
    map.get(sorted).push(str);
  }
}

function groupAnagrams(strs) {
  const map = new Map();

  for (let str of strs) {
    const sorted = str.split('').sort().join('');

    if (!map.has(sorted)) {
      map.set(sorted, []);
    }

    map.get(sorted).push(str);
  }

  // Return all groups as an array
  return Array.from(map.values());
}
