def group_anagrams(strs):
    groups = {}

    for str in strs:
        sorted_key = tuple(sorted(str))

        if sorted_key not in groups:
            groups[sorted_key] = []

        groups[sorted_key].append(str)

    # Return all groups as a list
    return list(groups.values())

