    for (String str : strs) {
        char[] chars = str.toCharArray();
        Arrays.sort(chars);
        String sorted = new String(chars);

        if (!map.containsKey(sorted)) {
            map.put(sorted, new ArrayList<>());
        }

        // Add original string to the group
        map.get(sorted).add(str);
    }
