  function getLength(head) {
    let length = 0;
    let current = head;
    while (current !== null) {
      length++;
      current = current.next;
    }
    return length;
  }

  let lenA = getLength(headA);
  let lenB = getLength(headB);
  let diff = Math.abs(lenA - lenB);
  let ptrA = headA;
  let ptrB = headB;

  // Move longer list's pointer ahead
  if (lenA > lenB) {
    for (let i = 0; i < diff; i++) {
      ptrA = ptrA.next;
    }
  } else {
    for (let i = 0; i < diff; i++) {
      ptrB = ptrB.next;
    }
  }

}

function getIntersectionNode(headA, headB) {
  function getLength(head) {
    let length = 0;
    let current = head;
    while (current !== null) {
      length++;
      current = current.next;
    }
    return length;
  }

  let lenA = getLength(headA);
  let lenB = getLength(headB);
  let diff = Math.abs(lenA - lenB);
  let ptrA = headA;
  let ptrB = headB;

  if (lenA > lenB) {
    for (let i = 0; i < diff; i++) {
      ptrA = ptrA.next;
    }
  } else {
    for (let i = 0; i < diff; i++) {
      ptrB = ptrB.next;
    }
  }

  // Move both pointers together
  while (ptrA !== null && ptrB !== null) {
    if (ptrA === ptrB) {
      return ptrA;  // Found intersection!
    }
    ptrA = ptrA.next;
    ptrB = ptrB.next;
  }

}

function getIntersectionNode(headA, headB) {
  function getLength(head) {
    let length = 0;
    let current = head;
    while (current !== null) {
      length++;
      current = current.next;
    }
    return length;
  }

  let lenA = getLength(headA);
  let lenB = getLength(headB);
  let diff = Math.abs(lenA - lenB);
  let ptrA = headA;
  let ptrB = headB;

  if (lenA > lenB) {
    for (let i = 0; i < diff; i++) {
      ptrA = ptrA.next;
    }
  } else {
    for (let i = 0; i < diff; i++) {
      ptrB = ptrB.next;
    }
  }

  while (ptrA !== null && ptrB !== null) {
    if (ptrA === ptrB) {
      return ptrA;
    }
    ptrA = ptrA.next;
    ptrB = ptrB.next;
  }

  // No intersection found
  return null;
}
