function canJump(nums) {
  let maxReach = 0;

  for (let i = 0; i < nums.length; i++) {
    if (i > maxReach) {
      return false;
    }

    // Update maxReach to farthest we can reach from here
    maxReach = Math.max(maxReach, i + nums[i]);

  }
}

function canJump(nums) {
  let maxReach = 0;

  for (let i = 0; i < nums.length; i++) {
    if (i > maxReach) {
      return false;
    }

    maxReach = Math.max(maxReach, i + nums[i]);

    // If we can reach last index, return true
    if (maxReach >= nums.length - 1) {
      return true;
    }
  }

  return false;
}
