function longestConsecutive(nums) {
  let numSet = new Set(nums);
  let maxLen = 0;

  for (let num of numSet) {
    if (!numSet.has(num - 1)) {
      // Count consecutive sequence starting from num
      let currentNum = num;
      let currentLen = 1;

      while (numSet.has(currentNum + 1)) {
        currentNum++;
        currentLen++;
      }
    }
  }
}

function longestConsecutive(nums) {
