function lengthOfLongestSubstring(s) {
  let left = 0;
  let maxLen = 0;
  const charMap = new Map();

  for (let right = 0; right < s.length; right++) {
    const char = s[right];

    if (charMap.has(char) && charMap.get(char) >= left) {
      left = charMap.get(char) + 1;
