function removeDuplicates(nums) {
  if (nums.length === 0) return 0;

  let writeIndex = 1;

  for (let i = 1; i < nums.length; i++) {
    // Compare with last unique element
    if (nums[i] !== nums[writeIndex - 1]) {
      // Found a new unique element!
      nums[writeIndex] = nums[i];
      writeIndex++;
    }
  }

  return writeIndex;
}

