    // Move pointers here
  }
}

function reverseString(s) {
  let left = 0;
  let right = s.length - 1;

  while (left < right) {
    let temp = s[left];
    s[left] = s[right];
    s[right] = temp;

    // Move pointers inward
    left++;
    right--;
  }
}

