function mySqrt(x) {
  if (x === 0 || x === 1) {
    return x;
  }

  let left = 1;
  let right = x;

  while (left <= right) {
    let mid = Math.floor((left + right) / 2);

    if (mid * mid === x) {
      return mid;
    }

    // Update search range
    if (mid * mid > x) {
      right = mid - 1;
    } else {
      left = mid + 1;
    }
  }

}

function mySqrt(x) {
  if (x === 0 || x === 1) {
    return x;
  }

  let left = 1;
  let right = x;

  while (left <= right) {
    let mid = Math.floor((left + right) / 2);

    if (mid * mid === x) {
      return mid;
    }

    if (mid * mid > x) {
      right = mid - 1;
    } else {
      left = mid + 1;
    }
  }

  // Return right (largest integer where square <= x)
  return right;
}

