@Component({
  selector: 'app-example',
  template: '<h1>Hello</h1>',
  standalone: true
})
export class ExampleComponent {}

// @Input decorator example
class UserCardComponent {
  @Input() name!: string;  // Receives data
  @Input() age?: number;   // Optional input
}

// Usage:
<app-user-card [name]="'John'" />

// Component
export class AppComponent {
  userName = 'Alice';
}

// Template
<app-user-card [name]="userName" />

